// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.20 <0.9.0;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

// 导入您的代币合约接口
interface IMetaCoin {
    function transfer(address to, uint256 amount) external returns (bool);
    function transferFrom(
        address from,
        address to,
        uint256 amount
    ) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
    function allowance(
        address owner,
        address spender
    ) external view returns (uint256);
}

contract CourseMarket is Ownable, ReentrancyGuard {
    // 固定的代币合约地址
    IMetaCoin public constant metaCoinToken =
        IMetaCoin(0x521CDB596fEbCadE11c70899569338f22f631D55);

    // 课程结构体
    struct Course {
        uint256 courseId;
        string title;
        string description;
        uint256 price; // 以 syf 代币计价
        bool isActive;
        uint256 totalPurchases;
    }

    // 学生购买记录
    struct Purchase {
        uint256 courseId;
        address student;
        uint256 purchaseTime;
        uint256 paidAmount;
    }

    // 写死的课程数据
    Course[5] public courses;

    // 记录学生是否已购买某课程
    mapping(uint256 => mapping(address => bool)) public hasPurchased;

    // 记录每个课程的购买者列表
    mapping(uint256 => address[]) public coursePurchasers;

    // 记录每个学生的购买历史
    mapping(address => Purchase[]) public studentPurchases;

    // 事件
    event CoursePurchased(
        uint256 indexed courseId,
        address indexed student,
        uint256 price,
        uint256 timestamp
    );

    constructor() {
        // 初始化5门固定课程
        courses[0] = Course({
            courseId: 1,
            title: "course 1",
            description: "course 1 desc",
            price: 5,
            isActive: true,
            totalPurchases: 0
        });

        courses[1] = Course({
            courseId: 2,
            title: "course 2",
            description: "course 2 desc",
            price: 5,
            isActive: true,
            totalPurchases: 0
        });

        courses[2] = Course({
            courseId: 3,
            title: "course 3",
            description: "course 3 desc",
            price: 5,
            isActive: true,
            totalPurchases: 0
        });

        courses[3] = Course({
            courseId: 4,
            title: "course 4",
            description: "course 4 desc",
            price: 5,
            isActive: true,
            totalPurchases: 0
        });

        courses[4] = Course({
            courseId: 5,
            title: "course 5",
            description: "course 5 desc",
            price: 5,
            isActive: true,
            totalPurchases: 0
        });
    }

    // 购买课程（需要先approve代币）
    function purchaseCourse(uint256 _courseId) external nonReentrant {
        require(_courseId >= 1 && _courseId <= 5, "Invalid course ID");
        uint256 courseIndex = _courseId - 1; // 数组索引从0开始

        Course storage course = courses[courseIndex];
        require(course.isActive, "Course is not active");
        require(
            !hasPurchased[_courseId][msg.sender],
            "Already purchased this course"
        );

        uint256 coursePrice = course.price;
        require(
            metaCoinToken.balanceOf(msg.sender) >= coursePrice,
            "Insufficient token balance"
        );
        require(
            metaCoinToken.allowance(msg.sender, address(this)) >= coursePrice,
            "Please approve tokens first. Call approve() on token contract"
        );

        // 将代币转账到合约地址（不分配收益）
        require(
            metaCoinToken.transferFrom(msg.sender, address(this), coursePrice),
            "Token transfer failed"
        );

        // 记录购买
        hasPurchased[_courseId][msg.sender] = true;
        coursePurchasers[_courseId].push(msg.sender);
        course.totalPurchases++;

        // 添加到学生购买历史
        studentPurchases[msg.sender].push(
            Purchase({
                courseId: _courseId,
                student: msg.sender,
                purchaseTime: block.timestamp,
                paidAmount: coursePrice
            })
        );

        emit CoursePurchased(
            _courseId,
            msg.sender,
            coursePrice,
            block.timestamp
        );
    }

    // 管理员提取合约中的代币
    function withdrawTokens(uint256 _amount) external onlyOwner {
        require(
            metaCoinToken.balanceOf(address(this)) >= _amount,
            "Insufficient contract balance"
        );
        require(
            metaCoinToken.transfer(owner(), _amount),
            "Token transfer failed"
        );
    }

    // 查询函数

    // 获取所有课程信息
    function getAllCourses() external view returns (Course[] memory) {
        Course[] memory allCourses = new Course[](5);

        for (uint256 i = 0; i < 5; i++) {
            allCourses[i] = courses[i];
        }

        return allCourses;
    }

    // 获取单个课程详情
    function getCourse(
        uint256 _courseId
    )
        external
        view
        returns (
            string memory title,
            string memory description,
            uint256 price,
            bool isActive,
            uint256 totalPurchases
        )
    {
        require(_courseId >= 1 && _courseId <= 5, "Invalid course ID");
        uint256 courseIndex = _courseId - 1;
        Course memory course = courses[courseIndex];

        return (
            course.title,
            course.description,
            course.price,
            course.isActive,
            course.totalPurchases
        );
    }

    // 获取课程购买者列表
    function getCoursePurchasers(
        uint256 _courseId
    ) external view returns (address[] memory) {
        require(_courseId >= 1 && _courseId <= 5, "Invalid course ID");
        return coursePurchasers[_courseId];
    }

    // 获取课程购买者数量
    function getCoursePurchasersCount(
        uint256 _courseId
    ) external view returns (uint256) {
        require(_courseId >= 1 && _courseId <= 5, "Invalid course ID");
        return coursePurchasers[_courseId].length;
    }

    // 检查用户是否已购买某课程
    function hasUserPurchased(
        uint256 _courseId,
        address _user
    ) external view returns (bool) {
        return hasPurchased[_courseId][_user];
    }

    // 获取学生的购买历史
    function getStudentPurchases(
        address _student
    ) external view returns (Purchase[] memory) {
        return studentPurchases[_student];
    }

    // 获取学生购买的课程数量
    function getStudentPurchaseCount(
        address _student
    ) external view returns (uint256) {
        return studentPurchases[_student].length;
    }

    // 检查特定地址在特定课程的购买状态
    function checkPurchaseStatus(
        uint256 _courseId,
        address _student
    )
        external
        view
        returns (bool purchased, uint256 purchaseTime, uint256 paidAmount)
    {
        if (!hasPurchased[_courseId][_student]) {
            return (false, 0, 0);
        }

        Purchase[] memory purchases = studentPurchases[_student];
        for (uint256 i = 0; i < purchases.length; i++) {
            if (purchases[i].courseId == _courseId) {
                return (
                    true,
                    purchases[i].purchaseTime,
                    purchases[i].paidAmount
                );
            }
        }

        return (false, 0, 0);
    }

    // 获取合约代币余额
    function getContractTokenBalance() external view returns (uint256) {
        return metaCoinToken.balanceOf(address(this));
    }

    // 获取用户代币余额
    function getUserTokenBalance(
        address _user
    ) external view returns (uint256) {
        return metaCoinToken.balanceOf(_user);
    }

    // 获取用户对合约的授权额度
    function getUserAllowance(address _user) external view returns (uint256) {
        return metaCoinToken.allowance(_user, address(this));
    }

    // 紧急函数：提取误转的其他代币
    function emergencyWithdrawTokens(
        address _token,
        uint256 _amount
    ) external onlyOwner {
        require(_token != address(0), "Invalid token address");
        require(
            _token != address(metaCoinToken),
            "Use withdrawTokens for main token"
        );
        IMetaCoin token = IMetaCoin(_token);
        require(token.transfer(owner(), _amount), "Token transfer failed");
    }
}
