// SPDX-License-Identifier: GPL-3.0
pragma solidity >=0.8.20 <0.9.0;

contract RedEnvelope {
    struct Pack {
        address payable owner;
        uint256 totalAmount; //总金额
        uint256 count; //总个数
        bool isEqual; // 等额红包
        mapping(address => bool) isGrabbed; // 是否抢过
    }

    struct PackInfo {
        address owner;
        uint256 totalAmount;
        uint256 count;
        bool isEqual;
        uint256 packId; // 红包ID
    }

    Pack[] internal packs; // 红包列表

    function deposit(uint256 _count, bool _isEqual) public payable {
        require(msg.value > 0, "must > 0");

        // 检查是否已存在同个owner的红包
        for (uint256 i = 0; i < packs.length; i++) {
            if (packs[i].owner == msg.sender && packs[i].totalAmount > 0) {
                // 如果找到同个owner的红包，返回剩余金额并重置红包
                uint256 remainingAmount = packs[i].totalAmount;
                packs[i].totalAmount = 0;
                packs[i].count = 0;

                // 返还之前的剩余金额
                payable(msg.sender).transfer(remainingAmount);
                break;
            }
        }

        packs.push();
        Pack storage newPack = packs[packs.length - 1];
        newPack.owner = payable(msg.sender);
        newPack.count = _count;
        newPack.totalAmount = msg.value;
        newPack.isEqual = _isEqual;
    }

    function findPackByOwner(address _owner) internal view returns (uint) {
        for (uint i = 0; i < packs.length; i++) {
            if (
                packs[i].owner == _owner &&
                packs[i].count > 0 &&
                packs[i].totalAmount > 0
            ) {
                return i;
            }
        }
        revert("Not found");
    }

    function grabbedRedPack(address _owner) public {
        uint256 index = findPackByOwner(_owner);
        Pack storage p = packs[index];

        require(p.count > 0, "count must > 0");
        require(p.totalAmount > 0, "totalAmount must > 0");
        require(!p.isGrabbed[msg.sender], "you have grabbed");

        if (p.count == 1) {
            payable(msg.sender).transfer(p.totalAmount);
            p.totalAmount = 0;
        } else {
            if (p.isEqual) {
                uint256 amount = p.totalAmount / p.count;

                p.totalAmount -= amount;
                payable(msg.sender).transfer(amount);
            } else {
                uint256 random = (uint256(
                    keccak256(
                        abi.encodePacked(block.timestamp, msg.sender, p.count)
                    )
                ) % 8) + 1;

                uint256 amount = (p.totalAmount * random) / 10;
                p.totalAmount -= amount;
                payable(msg.sender).transfer(amount);
            }
        }

        p.isGrabbed[msg.sender] = true;
        p.count--;
    }

    function getPackList() public view returns (PackInfo[] memory) {
        PackInfo[] memory packInfos = new PackInfo[](packs.length);

        for (uint256 i = 0; i < packs.length; i++) {
            packInfos[i] = PackInfo({
                owner: packs[i].owner,
                totalAmount: packs[i].totalAmount,
                count: packs[i].count,
                isEqual: packs[i].isEqual,
                packId: i
            });
        }

        return packInfos;
    }
}
